mapper.select:
SELECT id,	planid,	userid,	parentid,	comment_time,	content,	rate,	comment_type,	deleteflag
FROM comment
WHERE id = #{id}

mapper.update:
UPDATE comment
SET 	planid=#{planid},	userid=#{userid},	parentid=#{parentid},	comment_time=#{comment_time},	content=#{content},	rate=#{rate},	comment_type=#{comment_type},	deleteflag=#{deleteflag}
WHERE id = #{id}
