mapper.select:
SELECT id,	promulgatorid,	name,	starttime,	endtime,	introduction,	cover,	location,	venueid,	overdue,	isrecommend,	type,	scheme,	state
FROM plan
WHERE state =#{state}
ORDER BY starttime DESC
limit #{offset_row} , #{page_size}

mapper.update:
UPDATE plan
set state =#{state}
where id =#{id}

UPDATE plan
set isrecommend =#{isrecommend}
where id =#{id}

mapper.select:
SELECT COUNT (id)
FROM plan
WHERE state =#{state}
